import React from "react";

const history = [
  { type: "Покупка", detail: "в магазине", points: "+150" },
  { type: "Баллы начислены", detail: "Посещение", points: "+200" },
  { type: "Подарок другу", detail: "Покупка", points: "+250" },
  { type: "Регистрация", detail: "Посещение", points: "+500" },
];

export default function History() {
  return (
    <div className="screen history">
      <h2>История</h2>
      <ul className="history-list">
        {history.map((item, idx) => (
          <li key={idx} className="history-item">
            <div>
              <b>{item.type}</b>
              <span className="history-detail">{item.detail}</span>
            </div>
            <div className="history-points">{item.points}</div>
          </li>
        ))}
      </ul>
    </div>
  );
}
