import React, { useState } from "react";

const segments = [
  { label: "50 –±–∞–ª–ª–æ–≤", icon: "üéâ" },
  { label: "100 –±–∞–ª–ª–æ–≤", icon: "üíé" },
  { label: "–ü—É—Å—Ç–æ", icon: "üôà" },
  { label: "–ü—Ä–∏–∑!", icon: "üéÅ" },
  { label: "25 –±–∞–ª–ª–æ–≤", icon: "üåü" },
  { label: "150 –±–∞–ª–ª–æ–≤", icon: "üèÜ" },
];

export default function WheelOfFortune() {
  const [spinning, setSpinning] = useState(false);
  const [selected, setSelected] = useState(null);
  const [rotation, setRotation] = useState(0);

  function spin() {
    if (spinning) return;
    setSpinning(true);
    setSelected(null);

    const segmentCount = segments.length;
    const prize = Math.floor(Math.random() * segmentCount);
    const turns = 5 + Math.random() * 1.5; // –∫—Ä–∞—Å–∏–≤–æ–µ –∫–æ–ª–∏—á–µ—Å—Ç–≤–æ –æ–±–æ—Ä–æ—Ç–æ–≤
    const angle = 360 / segmentCount;
    const finalRotation = 360 * turns - prize * angle - angle / 2;

    setRotation(finalRotation);

    setTimeout(() => {
      setSelected(prize);
      setSpinning(false);
    }, 2500);
  }

  const segmentAngle = 360 / segments.length;
  const radius = 90; // –†–∞–¥–∏—É—Å —Ä–∞–∑–º–µ—â–µ–Ω–∏—è —Å–µ–∫—Ç–æ—Ä–æ–≤

  return (
    <div className="wheel-container">
      <div className="wheel-bg-circle" />
      <div
        className={`wheel${spinning ? " spinning" : ""}`}
        style={{ transform: `rotate(${rotation}deg)` }}
      >
        {segments.map((s, i) => {
          const rotate = i * segmentAngle;
          return (
            <div
              key={i}
              className="wheel-segment"
              style={{
                transform: `rotate(${rotate}deg) translate(0, -${radius}px)`,
                background: i % 2 === 0 ? "#e0d1ff" : "#ffebef",
              }}
            >
              <span className="wheel-icon">{s.icon}</span>
              <span className="wheel-label">{s.label}</span>
            </div>
          );
        })}
      </div>
      <div className="wheel-pointer">‚ñ≤</div>
      <button className="wheel-spin-btn" onClick={spin} disabled={spinning}>
        {spinning ? "–ö—Ä—É—Ç–∏–º..." : "–ö—Ä—É—Ç–∏—Ç—å –∫–æ–ª–µ—Å–æ"}
      </button>
      {selected !== null && (
        <div className="wheel-result">
          –í–∞—à –ø—Ä–∏–∑: <b>{segments[selected].icon} {segments[selected].label}</b>
        </div>
      )}
    </div>
  );
}
